import { useEffect, useState, useRef } from "react";
import { Container } from "react-bootstrap";
import Calendar from 'react-calendar';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function PCalendar() {
    const username = JSON.parse(localStorage.getItem("user"));
    const [userDData,setUDData]=useState("");
    const isLoading = useRef(true); // Loading state
    const getdates = async () => {
        let DateResult = await fetch("http://localhost:3001/getuserprojectstatus", {
            method: 'post',
            body: JSON.stringify({
                name: username.name
            }),
            headers: {
                'Content-type': 'application/json'
            }
        });

        DateResult = await DateResult.json();


        if (DateResult) {
            console.warn("date " + DateResult[0].project_id[0].deadline);
            setUDData(DateResult);
            isLoading.current = false;

        }

    };

    useEffect(() => {
        if (isLoading.current) {
            getdates();

            isLoading.current = false;

        }

    }, []);

   

    const addevent = ()=>{
        alert("on event added");
    };

    if(userDData)
    {
        
    return (
        
            <Container>
                <Calendar onClickDay={addevent} 
                tileContent={( { date, view } ) => view === 'month' && date.getDate() === new Date(userDData[0].project_id[0].deadline).ge ? <p>It's Sunday!</p> : null }>
                    
                </Calendar>
            </Container>
    );
    }
    else
    {
      return <div>data is loading</div>
    }
}

export default PCalendar;
