import './signup.css'
import {useNavigate} from 'react-router-dom'
import React, { useState } from 'react';

function SignUp() {
    const [name, setName] = useState("");
    
    const [age, setAge] = useState("");
    const navigate =useNavigate();
    const collectData=async ()=>
    {
        console.warn(name,age);
        let result =await fetch('http://localhost:3001/register',{
            method:'post',
            body:JSON.stringify({name, age}),
            headers:{
                'Content-Type':'application/json'
            }

        });
        result = await result.json();
        console.warn(result);
        localStorage.setItem("user",json.stringify(result));
        if(result)
        {
            navigate('/');
        }
    }
    return (
        <div><h1>Registration Form</h1>
                <label className="signup_label">Enter Name</label>
                <input type='text' onChange={(e) => setName(e.target.value)}></input>
                <label className="signup_label">Enter Age</label>
                <input type='text' onChange={(e) => setAge(e.target.value)}></input>
                <button className="signup_label" type="button" onClick={collectData}>signup</button>
        </div>
    );
}

export default SignUp;
