import './signup.css'

import React, { useState } from 'react';

function SignUp() {
    const [name, setName] = useState("");
    
    const [age, setAge] = useState("");
    const collectData=()=>
    {
        console.warn(name,age);
        const result = fetch('http://localhost:3001/register',{
            method:'post',
            body:JSON.stringify({name, age})

        });
    }
    return (
        <div><h1>Registration Form</h1>
            <form onSubmit={collectData}>
                <label className="signup_label">Enter Name</label>
                <input type='text' onChange={(e) => setName(e.target.value)}></input>
                <label className="signup_label">Enter Age</label>
                <input type='text' onChange={(e) => setAge(e.target.value)}></input>
                <input className="signup_label" type="submit"></input>
            </form>
        </div>
    );
}

export default SignUp;
