import { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import Calendar from 'react-calendar';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function PCalendar() {
    const username = JSON.parse(localStorage.getItem("user"));

    const getdeadlines = async ()=>{

        let deadlinesResult  = await fetch("http://localhost:3001/getuserprojectstatus",{
            method: "post",
            body: JSON.stringify({
                name: username.name,
            }),
            headers: {
                'Content-type': 'application/json'
            }
        });

        deadlinesResult = await deadlinesResult.json();
        console.warn("before da " + deadlinesResult);
        if (deadlinesResult) {
            setUPdata(deadlinesResult);

        }
    };

    const addevent = (v, e) => {
        alert(v);
        console.warn(v);
        
    };  
    const d = new Date();
    // d.setFullYear(2023,2,26);

    return (
        <Container>
            <Calendar onClickDay={addevent} tileContent={({ date, view }) => view === 'month' && date.getDate() === 26 && date.getMonth()===2  ? <p>It's Sunday! { 5+d}</p> : null}></Calendar>
            {/* <Calendar onClickDay={addevent} tileContent={({ date, view }) => view === 'month'  ? <p>It's Sunday! {d}</p> : null}></Calendar> */}

            <Popup trigger={<button className="button"> Add Event </button>} modal> 
               <span> Modal content </span>
                 </Popup>
        </Container>
    );

};

export default PCalendar;
