import { useEffect, useRef, useState } from "react";
import { Container, Row, Col, ListGroup, ListGroupItem, Button } from "react-bootstrap";
import './message.css';
function Message() {
    const username = JSON.parse(localStorage.getItem("user"));
    const [userMessage, setUMesg] = useState("");
    const isLoading = useRef(true); // Loading state
    // console.warn("uname "+username.name);
    const getUmessage = async () => {

        let userMData = await fetch("http://localhost:3001/getusermessage", {
            method: "post",
            body: JSON.stringify({
                name: username.name
            }),
            headers: {
                'Content-type': 'application/json'
            }

        })

        userMData = await userMData.json();
        if (userMData) {
            // setUMesg(userMData);     
            console.warn("umsg " + userMData[0].project_id[0].message[0].message_body)
            isLoading.current= false;
            showmessage();
        }
    };
    useEffect(()=>{
        if(isLoading)
        {
        getUmessage();
        }
    },[]); 
    
    // useEffect(()=>{
       
    //     showmessage();
    // },[]);
    const showmessage = () => {
        alert("sshow message ");
        if(userMessage)
        alert("its working ");

        // if (typeof (userMessage[0]) !== 'undefined' && userMessage[0] != null) {
        //     alert("data set " + userMessage[0].project_id[0].message[0].message_body)

        // }
    };

    if (isLoading) {
        return (
            <div>Loading hold on
                {/* <button onClick={getUmessage}> get api</button> */}

            </div>
        );
    }
    else {
        return (
            <Container className="messageContainer">
                {/* <button onClick={getUmessage}> get api</button> */}
                <ListGroup>
                    <ListGroupItem className="messageListItem">Jaysing</ListGroupItem>
                    <ListGroupItem className="messageListItem">Siddhant</ListGroupItem>
                    <ListGroupItem className="messageListItem">Kunal</ListGroupItem>
                </ListGroup>

                <Container className="messageBody">
                    <span className="messageReceived ">
                        <span className="messageReceivedBody">jaysing hfkjfdf kfdf f dskfh dfhfj fkjfhdkjfd</span>
                        <span class="messageReceivedTime ">12:00</span>
                    </span>

                    <span className="messageSend ">
                        <span className="messageSendBody">jaysing hfkjfdf kfdf f dskfh dfhfj fkjfhdkjfd</span>
                        <span class="messageSendTime ">1:00</span>
                    </span>

                </Container>
                <Container className="messageBar ">
                    <input className="messageInput" type="text" placeholder="Message"></input>
                    <Button></Button>
                </Container>

            </Container>
        );
    }
}

export default Message;
