import { Container } from "react-bootstrap";
import Calendar from 'react-calendar';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function PCalendar() {

    const addevent = (v, e) => {
        alert(v);
        console.warn(v);

        // return (

        // );
    };



    return (
        <Container>
            <Calendar onClickDay={addevent} tileContent={({ date, view }) => ('')}></Calendar>
            <div>
            <h4>Popup - GeeksforGeeks</h4>
            <Popup trigger=
                {<button> Click to open modal </button>}
                modal nested>
                {
                    close => (
                        <div className='modal'>
                            <div className='content'>
                                Welcome to GFG!!!
                            </div>
                            <div>
                                <button onClick=
                                    {() => close()}>
                                        Close modal
                                </button>
                            </div>
                        </div>
                    )
                }
            </Popup>
        </div>
        </Container>
    );

};

export default PCalendar;
