import { Container } from "react-bootstrap";
import Calendar from 'react-calendar';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function PCalendar() {

    const getdates = async ()=>{
        let DateResult = await fetch("http://localhost:3001/getuserprojectstatus",{
            method: 'post',
            body: JSON.stringify({
                name: projectName,
                desc: projectDesc,
                devl_id: multiDevSel,
                catg: projectType,
                deadline: projectDeadline,
                message: [{
                    message_body: "hello",
                    message_sender: "manager",
                    time:new Date()
                }],
                created: new Date(),
                pstatus: "incomp"
            }),
            headers: {
                'Content-type': 'application/json'
            }
        });

    };

    return (
    <div>calendar</div>
    );
}

export default PCalendar;
